import sqlite3

def setup_database(db_name='database.db'):
    conn = sqlite3.connect(db_name)
    cursor = conn.cursor()

    # Create bands table
    cursor.execute('''
    CREATE TABLE IF NOT EXISTS bands (
        name TEXT PRIMARY KEY,
        hometown TEXT
    )
    ''')

# Create venues table
    cursor.execute('''    
    CREATE TABLE IF NOT EXISTS venues (
        title TEXT PRIMARY KEY,
        city TEXT
    )    
    '''
    )

    # Create concerts table
    cursor.execute('''
CREATE TABLE IF NOT EXISTS concerts (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        band_name TEXT,
        venue_title TEXT,
        date TEXT,
        FOREIGN KEY (band_name) REFERENCES bands (name),
FOREIGN KEY (venue_title) REFERENCES venues (title)
    )
    ''')

    # Insert data
    cursor.execute("INSERT OR IGNORE INTO bands (name, hometown) VALUES ('The Champions', 'Nashville')")
    cursor.execute("INSERT OR IGNORE INTO venues (title, city) VALUES ('Southampton', 'Nashville')")
    cursor.execute("INSERT OR IGNORE INTO concerts (band_name, venue_title, date) VALUES ('The Champions', 'Southampton', '2024-10-11')")

    conn.commit()
    conn.close()

if __name__ == "__main__":
    setup_database()
