import sqlite3

class Divide:
    def __init__(self, db_name='database.db'):
        self.db_name = db_name

    def concert_band(self, concert_id):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute('''
        SELECT band_name FROM concerts WHERE id = ?
''', (concert_id,))
        band = cursor.fetchone()
        conn.close()
        return band[0] if band else None

    def concert_venue(self, concert_id):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute(''' 
        SELECT venue_title FROM concerts WHERE id = ?
        ''', (concert_id,))
        venue = cursor.fetchone()
        conn.close()
        return venue[0] if venue else None

def venue_concerts(self, venue_title):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute(''' 
        SELECT * FROM concerts WHERE venue_title = ?
        ''', (venue_title,))
        concerts = cursor.fetchall()
        conn.close()
        return concerts

def venue_bands(self, venue_title):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute('''
SELECT DISTINCT band_name FROM concerts WHERE venue_title = ?
        ''', (venue_title,))
        bands = cursor.fetchall()
        conn.close()
        return [band[0] for band in bands]

def band_concerts(self, band_name):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute(''' 
        SELECT * FROM concerts WHERE band_name = ?
        ''', (band_name,))
        concerts = cursor.fetchall()
        conn.close()
        return concerts

def band_venues(self, band_name):
        conn = sqlite3.connect(self.db_name)
        cursor = conn.cursor()
        cursor.execute(''' 
        SELECT DISTINCT venue_title FROM concerts WHERE band_name = ?
''', (band_name,)) 
        venues = cursor.fetchall() 
        conn.close() 
        return [venue[0] for venue in venues] 



if __name__ == "__main__":
    divide = Divide()
    print("Concerts at 'Carnivore':")
    concerts = divide.venue_concerts('Big Arena')
    for concert in concerts:
        print(concert)
